class UI:
    def __init__(self, game):
        self.game = game
        self.screen = pygame.display.set_mode((self.game.snake_game.width, self.game.snake_game.height))

    def draw(self):
        self.screen.fill((0, 0, 0))
        for x, y in self.game.snake_game.snake:
            pygame.draw.rect(self.screen, (0, 255, 0), (x, y, 10, 10))
        if self.game.food:
            pygame.draw.rect(self.screen, (255, 0, 0), (self.game.food[0], self.game.food[1], 10, 10))
        pygame.display.update()
